import React, { Component } from 'react';

class Contact extends Component{

    constructor(props)
    {
        super(props);
        this.state = {
            name: "",
            email: "",
            message: ""
        };
    }

    handleNameChange = (event) => {
        this.setState({name: event.target.value});
    }

    handleEmailChange = (event) => {
        this.setState({email: event.target.value});
    }

    handleMessageChange = (event) => {
        this.setState({message: event.target.value});
    }


    uploadMessageToDatabase = (event) =>
    {
        
        fetch('/api/sendmessage' , {
            method: "POST",
            headers: {
                'Content-type': 'application/json'
            },
            body: JSON.stringify(this.state)
            })
            .then((result) => result.json())
            .then((info) => { console.log(info); });
            
    }

    validate (){
        if(!this.state.message == "")
        {
            alert("Please complete the message");
            return false; 
        }
        else
        {
            return true;
        }
    }




    render(){
        return (
            
        <div className="page"><section>
       <div id = "heading">
        <h1>Contact</h1><h1 class = "blinking">_</h1>   
        </div>            
        <hr class = "greenbar"></hr>
           <p></p>
           </section>
           <form>
            <label>
                Name:
                <input type="text" name="name" value={this.state.name} onChange={this.handleNameChange} required />
                <br></br>
                Email:
                <input type="text" name="name" value={this.state.email} onChange={this.handleEmailChange} required/>
                <br></br>
            </label>
            </form>  
            Message:          
            <textarea name="comment" form="message" value ={this.state.message} onChange={this.handleMessageChange}></textarea>
            
            <button value="Submit" disabled={this.validate()}  onClick={() => {this.uploadMessageToDatabase()}}> Submit </button>

           <hr class = "greenbar"></hr>
       </div>
    );
    }
}
 
export default Contact;
